def validate_signature(public_key, signature, msg):
    verifier = pkcs1_15.new(RSA.importKey(public_key))

    h_v = SHA256.new() 
    try:
        h_v.update(msg)
        verifier.verify(h_v,signature)
        return True
    except ValueError: 
        print("invalid signature")
        return False

